PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "django_migrations" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "app" varchar(255) NOT NULL, "name" varchar(255) NOT NULL, "applied" datetime NOT NULL);
CREATE TABLE IF NOT EXISTS "django_content_type" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "app_label" varchar(100) NOT NULL, "model" varchar(100) NOT NULL);
CREATE TABLE IF NOT EXISTS "auth_group_permissions" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "group_id" integer NOT NULL REFERENCES "auth_group" ("id") DEFERRABLE INITIALLY DEFERRED, "permission_id" integer NOT NULL REFERENCES "auth_permission" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "auth_permission" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "content_type_id" integer NOT NULL REFERENCES "django_content_type" ("id") DEFERRABLE INITIALLY DEFERRED, "codename" varchar(100) NOT NULL, "name" varchar(255) NOT NULL);
CREATE TABLE IF NOT EXISTS "auth_group" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(150) NOT NULL UNIQUE);
CREATE TABLE IF NOT EXISTS "user_useraccount" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "password" varchar(128) NOT NULL, "last_login" datetime NULL, "is_superuser" bool NOT NULL, "username" varchar(150) NOT NULL UNIQUE, "first_name" varchar(150) NOT NULL, "last_name" varchar(150) NOT NULL, "email" varchar(254) NOT NULL, "is_staff" bool NOT NULL, "is_active" bool NOT NULL, "date_joined" datetime NOT NULL, "address" varchar(30) NOT NULL, "phone" varchar(11) NOT NULL, "avatar" varchar(100) NOT NULL);
CREATE TABLE IF NOT EXISTS "user_useraccount_groups" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "useraccount_id" bigint NOT NULL REFERENCES "user_useraccount" ("id") DEFERRABLE INITIALLY DEFERRED, "group_id" integer NOT NULL REFERENCES "auth_group" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "user_useraccount_user_permissions" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "useraccount_id" bigint NOT NULL REFERENCES "user_useraccount" ("id") DEFERRABLE INITIALLY DEFERRED, "permission_id" integer NOT NULL REFERENCES "auth_permission" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "django_admin_log" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "action_time" datetime NOT NULL, "object_id" text NULL, "object_repr" varchar(200) NOT NULL, "change_message" text NOT NULL, "content_type_id" integer NULL REFERENCES "django_content_type" ("id") DEFERRABLE INITIALLY DEFERRED, "user_id" bigint NOT NULL REFERENCES "user_useraccount" ("id") DEFERRABLE INITIALLY DEFERRED, "action_flag" smallint unsigned NOT NULL CHECK ("action_flag" >= 0));
CREATE TABLE IF NOT EXISTS "monitor_devicecategory" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL, "index" integer NOT NULL);
CREATE TABLE IF NOT EXISTS "monitor_environmentindicator" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL);
CREATE TABLE IF NOT EXISTS "monitor_greenhouse" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL);
CREATE TABLE IF NOT EXISTS "monitor_environmentdata" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "value" decimal NOT NULL, "create_time" datetime NOT NULL, "device_id" bigint NULL REFERENCES "monitor_device" ("id") DEFERRABLE INITIALLY DEFERRED, "indicator_id" bigint NULL REFERENCES "monitor_environmentindicator" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "monitor_device" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL, "location" varchar(100) NOT NULL, "status" varchar(100) NOT NULL, "is_active" bool NOT NULL, "category_id" bigint NULL REFERENCES "monitor_devicecategory" ("id") DEFERRABLE INITIALLY DEFERRED, "greenhouse_id" bigint NULL REFERENCES "monitor_greenhouse" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "django_session" ("session_key" varchar(40) NOT NULL PRIMARY KEY, "session_data" text NOT NULL, "expire_date" datetime NOT NULL);
CREATE TABLE IF NOT EXISTS "controller_deviceorder" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL, "path" varchar(100) NOT NULL, "parameter" varchar(100) NOT NULL, "device_id" bigint NULL REFERENCES "monitor_device" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "controller_bookcontrol" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "start_time" datetime NOT NULL, "stop_time" datetime NOT NULL, "device_id" bigint NULL REFERENCES "monitor_device" ("id") DEFERRABLE INITIALLY DEFERRED, "order_id" bigint NULL REFERENCES "controller_deviceorder" ("id") DEFERRABLE INITIALLY DEFERRED, "name" varchar(100) NOT NULL);
CREATE TABLE IF NOT EXISTS "controller_devicecontrol" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "control_id" integer NULL, "device_id" bigint NULL REFERENCES "monitor_device" ("id") DEFERRABLE INITIALLY DEFERRED, "status" varchar(10) NOT NULL, "control_type" integer NOT NULL);
CREATE TABLE IF NOT EXISTS "controller_controlhistory" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "control_type" integer NOT NULL, "device_id" bigint NULL REFERENCES "monitor_device" ("id") DEFERRABLE INITIALLY DEFERRED, "order_id" bigint NULL REFERENCES "controller_deviceorder" ("id") DEFERRABLE INITIALLY DEFERRED, "create_time" datetime NOT NULL);
CREATE TABLE IF NOT EXISTS "controller_timercontrol" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "start_time" datetime NOT NULL, "stop_time" datetime NOT NULL, "device_id" bigint NULL REFERENCES "monitor_device" ("id") DEFERRABLE INITIALLY DEFERRED, "order_id" bigint NULL REFERENCES "controller_deviceorder" ("id") DEFERRABLE INITIALLY DEFERRED, "name" varchar(100) NOT NULL, "repeat_type" varchar(10) NOT NULL);
CREATE TABLE IF NOT EXISTS "controller_thresholdcontrol" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "threshold" decimal NOT NULL, "indicator_id" bigint NULL REFERENCES "monitor_environmentindicator" ("id") DEFERRABLE INITIALLY DEFERRED, "order_id" bigint NULL REFERENCES "controller_deviceorder" ("id") DEFERRABLE INITIALLY DEFERRED, "name" varchar(100) NOT NULL, "device_id" bigint NULL REFERENCES "monitor_device" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "forum_banner" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "text_info" varchar(50) NOT NULL, "img" varchar(100) NOT NULL, "link_url" varchar(100) NOT NULL, "is_active" bool NOT NULL);
CREATE TABLE IF NOT EXISTS "forum_category" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL, "index" integer NOT NULL);
CREATE TABLE IF NOT EXISTS "forum_tag" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL);
CREATE TABLE IF NOT EXISTS "forum_tui" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL);
CREATE TABLE IF NOT EXISTS "forum_comment" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "comment_content" text NOT NULL, "create_time" datetime NOT NULL, "update_time" datetime NOT NULL, "is_delete" bool NOT NULL, "article_id" bigint NOT NULL REFERENCES "forum_article" ("id") DEFERRABLE INITIALLY DEFERRED, "user_id" bigint NOT NULL REFERENCES "user_useraccount" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "forum_article_tags" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "article_id" bigint NOT NULL REFERENCES "forum_article" ("id") DEFERRABLE INITIALLY DEFERRED, "tag_id" bigint NOT NULL REFERENCES "forum_tag" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "forum_article" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "title" varchar(70) NOT NULL, "excerpt" text NOT NULL, "img" varchar(100) NULL, "body" text NOT NULL, "views" integer unsigned NOT NULL CHECK ("views" >= 0), "created_time" datetime NOT NULL, "modified_time" datetime NOT NULL, "category_id" bigint NULL REFERENCES "forum_category" ("id") DEFERRABLE INITIALLY DEFERRED, "tui_id" bigint NULL REFERENCES "forum_tui" ("id") DEFERRABLE INITIALLY DEFERRED, "user_id" bigint NOT NULL REFERENCES "user_useraccount" ("id") DEFERRABLE INITIALLY DEFERRED);
